mapper.update:
update individual_cert_apply
MEMBER_ID = #{memberId}, APPLY_TYPE = #{applyType}, NAME = #{name}, CERT_TYPE = #{certType}, CERT_CODE = #{certCode}, STATUS = #{status}, APPLY_TIME = #{applyTime}, CREATE_TIME = #{createTime}, CREATE_MEMBER_ID = #{createMemberId}, CREATE_ACCT_ID = #{createAcctId}, CREATE_NAME = #{createName}, APPROVE_ACCT_ID = #{approveAcctId}, APPROVE_NAME = #{approveName}, APPROVE_MEMBER_ID = #{approveMemberId}, AUDIT_TIME = #{auditTime}, APPROVE_DESC = #{approveDesc},
and CERT_APPLY_ID = #{certApplyId}

update individual_cert_apply
MEMBER_ID = #{memberId}, APPLY_TYPE = #{applyType}, NAME = #{name}, CERT_TYPE = #{certType}, CERT_CODE = #{certCode}, STATUS = #{status}, APPLY_TIME = #{applyTime}, CREATE_TIME = #{createTime}, CREATE_MEMBER_ID = #{createMemberId}, CREATE_ACCT_ID = #{createAcctId}, CREATE_NAME = #{createName}, AUDIT_TIME = #{auditTime}, APPROVE_ACCT_ID = #{approveAcctId}, APPROVE_NAME = #{approveName}, APPROVE_MEMBER_ID = #{approveMemberId}, APPROVE_DESC = #{approveDesc},
and CERT_APPLY_ID = #{certApplyId}

mapper.select:
SELECT bu.ACCT_ID acctId , bu.ACCT_NAME acctName , bu.ACCT_CODE acctCode , bu.ACCT_STATE acctState , bu.AUTH_STATE authState , bu.LOGO_URL logoUrl , bu.GENDER_TYPE genderType , bu.DEPT_ID deptId , bu.MOBILE_NUM mobileNum , bu.EMAIL email , bu.AREA_ID areaId , bu.AREA_ID_FULL_PATH areaIdFullPath , bu.AREA_NAME_FULL_PATH areaNameFullPath , bu.CONTACT_ADDR contactAddr , bu.POST_CODE postCode , bu.REGISTER_TIME registerTime ,
apl.CERT_APPLY_ID certApplyId , rel.MEMBER_ID memberId , apl.APPLY_TYPE applyType , apl.`NAME` `name`, apl.CERT_TYPE certType , apl.CERT_CODE certCode , apl.`STATUS` `status` , apl.APPLY_TIME applyTime , apl.CREATE_TIME createTime , apl.CREATE_NAME createName , apl.APPROVE_ACCT_ID approveAcctId , apl.APPROVE_NAME approveName , apl.APPROVE_MEMBER_ID approveMemberId , apl.AUDIT_TIME auditTime , apl.APPROVE_DESC approveDesc FROM busi_acct_info bu LEFT JOIN rela_member_to_acct rel ON bu.ACCT_ID = rel.ACCT_ID AND rel.MEMBER_TYPE = 3 LEFT JOIN INDIVIDUAL_CERT_APPLY apl ON rel.MEMBER_ID = apl.MEMBER_ID
limit #{startRow}, #{pageSize}
